};

const makeRect = (game, color1, width, height) =>
    new Phaser.Graphics(game)
        .beginFill(color1)
        .drawRect(0, 0, width, height)
        .endFill()
        .generateTexture();

export class DebugButton extends Phaser.Button {
    constructor(game, spec, isMobile = false) {
        super(game);

        //TODO heights are hard coded. Probably not an issue with final image based buttons.
        this.backdrops = {
            mobile: {
                up: makeRect(game, 0xf6931e, spec.width, 42),
                over: makeRect(game, 0xffaa46, spec.width, 42),
            },
            desktop: {
                up: makeRect(game, 0xf6931e, spec.width, 64),
                over: makeRect(game, 0xffaa46, spec.width, 64),
            },
        };

        this.buttonSize = isMobile ? "mobile" : "desktop";

        this.texture = this.backdrops[this.buttonSize].up;

        this.animations.sprite.anchor.setTo(0.5, 0.5);
        this.onInputUp.add(spec.click, this);

        this.animations.sprite.events.onInputOver.add(() => {
            this.texture = this.backdrops[this.buttonSize].over;
        });

        this.animations.sprite.events.onInputOut.add(() => {
            this.texture = this.backdrops[this.buttonSize].up;
        });

        const text = new Phaser.Text(game, 0, 0, spec.text, gelStyle);
        text.anchor.setTo(0.5, 0.5);
        this.animations.sprite.addChild(text);
    }

     resize(metrics) {
        this.buttonSize = metrics.isMobile ? "mobile" : "desktop";
        this.texture = this.backdrops[this.buttonSize].up;
    }

    /**
     * Disables input and makes button semi-transparent
     */
    setEnabled(bool = true) {
        this.animations.sprite.inputEnabled = bool;
        this.animations.sprite.alpha = bool ? 1 : 0.5;
    }
}

import * as Scaler from "../scaler.js";
import { Layout } from "./layout.js";

export function create(game, gameWrapper) {
    const root = game.add.group(undefined, "gelGroup", true);
    const background = game.add.group(undefined, "gelBackground");
    const foreground = game.add.group(undefined, "foreground");
    const keyLookups = {};

    //TODO stageHeight should come from config
    const scaler = Scaler.create(600, game);

    root.addChild(background);
    root.addChild(foreground);

    scaler.onScaleChange.add(scaleBackground);

    return {
        keyLookups,
        addToBackground,
        addToForeground,
        addLayout,
        removeAll,
        addLookups,
        getSize: scaler.getSize,
    };

    /**
     * Create a new GEL layout manager for a given Genie {@link Screen}
     * Called in the create method of a given screen
     *
     * @example
     * layoutFactory.create(["home", "restart", "continue", "pause"]);
     *
     * @param buttons - array of standard button names to include. See {@link ./gel-defaults.ts} for available names
     * @returns {Layout}
     */
    function addLayout(buttons) {
        const layout = new Layout(game, scaler, buttons);

        addToBackground(layout.root);

        return layout;
    }

    function addToBackground(object) {
        if ((object).anchor) {
            (object).anchor.setTo(0.5, 0.5);
        }
        return background.addChild(object);
    }

    function addToForeground(object) {
        return foreground.addChild(object);
    }

    function scaleBackground(width, height, scale) {
        background.scale.set(scale, scale);
        background.position.set(width / 2, height / 2);
    }

    function removeAll() {
        background.removeAll(true);
        // buttons.removeAll();
    }

    function addLookups(moreLookups) {
        Object.assign(keyLookups, moreLookups);
    }
}

