import fp from "../../lib/lodash/fp/fp.js";

import * as ButtonFactory from "./button-factory.js";

const horizontal = {
    left: (pos, width, pad) => pos + pad,
    right: (pos, width, pad) => pos - width - pad,
    center: (pos, width, pad) => pos - width / 2,
};

const vertical = {
    top: (pos, height, pad) => pos + pad,
    middle: (pos, height, pad) => pos - height / 2,
    bottom: (pos, height, pad) => pos - (height + pad),
};

const getGroupPosition = sizes => ({
    x: getGroupX(sizes),
    y: getGroupY(sizes),
});

const getGroupX = sizes => {
    const horizontals = sizes.metrics[sizes.pos.v === "middle" ? "safeHorizontals" : "horizontals"];

    return horizontal[sizes.pos.h](horizontals[sizes.pos.h], sizes.width, sizes.metrics.borderPad * sizes.scale);
};

const getGroupY = sizes =>
    vertical[sizes.pos.v](sizes.metrics.verticals[sizes.pos.v], sizes.height, sizes.metrics.borderPad * sizes.scale);

export class Group extends Phaser.Group {
    constructor(game, parent, vPos, hPos, metrics, isVertical) {
        super(game, parent, fp.camelCase([vPos, hPos, isVertical ? "v" : ""].join(" ")));

        this._vPos = vPos;
        this._hPos = hPos;
        this._metrics = metrics;
        this._isVertical = isVertical;
        this._buttons = [];

        this._buttonFactory = ButtonFactory.create(game);
        this._setGroupPosition = fp.flow(this.getSizes, getGroupPosition, this.setPos);
        this._setGroupPosition();
    }

    /**
     * TODO add interface for config
     */
    addButton(config, position = this._buttons.length) {
        const newButton = this._buttonFactory.createButton(this._metrics.isMobile, config.key);

        this.addAt(newButton, position);
        this._buttons.push(newButton);

        this.alignChildren();
        this._setGroupPosition();

        return newButton;
    }

    addToGroup(item, position = 0) {
        item.anchor.setTo(0.5, 0.5);
        this.addAt(item, position);
        this.alignChildren();
        this._setGroupPosition();
    }

    reset(metrics) {
        if (this._metrics.isMobile !== metrics.isMobile) {
            this.resetButtons(metrics);
            this.alignChildren();
        }

        this._metrics = metrics;
        const invScale = 1 / metrics.scale;
        this.scale.setTo(invScale, invScale);
        this._setGroupPosition();
    }

    alignChildren() {
        const pos = { x: 0, y: 0 };

        const groupWidth = this.width; //Save here as size changes when you move children below
        this.children.forEach(childDisplayObject => {
            const child = childDisplayObject;
            child.y = pos.y + child.height / 2;

            if (this._isVertical) {
                child.x = groupWidth / 2;
                pos.y += child.height + this._metrics.buttonPad;
            } else {
                child.x = pos.x + child.width / 2;
                pos.x += child.width + this._metrics.buttonPad;
            }
        }, this);
    }

    //TODO this is currently observer pattern but will eventually use pub/sub Phaser.Signals
    resetButtons(metrics) {
        this._buttons.forEach(button => button.resize(metrics));
    }

    getSizes() {
        return {
            metrics: this._metrics,
            pos: { h: this._hPos, v: this._vPos },
            width: this.width,
            height: this.height,
            scale: this.scale.x,
        };
    }

    setPos({ x, y }) {
        this.x = x;
        this.y = y;
    }
}

import * as _ from "../../lib/lodash/lodash.js";

import { calculateMetrics } from "./calculate-metrics.js";
import gel from "./gel-defaults.js";
import { Group } from "./group.js";
import { groupLayouts } from "./group-layouts.js";
/**
 * Creates a new layout. Called by engine.create for each screen component
 *
 */
export class Layout {
    /**
     * @param game - Phaser Game Instance
     * @param {Scaler} scaler
     * @param keyLookup
     * @param buttons
     */
    constructor(game, scaler, buttons) {
        this.root = new Phaser.Group(game, game.world, undefined);

        const size = scaler.getSize();
        this.resize(size.width, size.height, size.scale, size.stageHeightPx);

        this._groups = _.zipObject(
            groupLayouts.map(layout => _.camelCase([layout.vPos, layout.hPos, layout.arrangeV ? "v" : ""].join(" "))),
            groupLayouts.map(
                layout => new Group(game, this.root, layout.vPos, layout.hPos, this._metrics, !!layout.arrangeV),
            ),
        );
        this.buttons = _.zipObject(buttons, buttons.map(name => this._groups[gel[name].group].addButton(gel[name])));

        scaler.onScaleChange.add(this.resize, this);
    }

    /**
     * Attach a callback to the onInputUp event of a given Gel button
     *
     * @param button- gel button identifier
     * @param callback - callback function to attach
     */
    setAction(button, callback) {
        this.buttons[button].onInputUp.add(callback, this);
    }

    addToGroup(groupName, item, position) {
        this._groups[groupName].addToGroup(item, position);
    }

    destroy() {
        this.root.destroy();
    }

    resize(width, height, scale, stageHeight) {
        this._metrics = calculateMetrics(width, height, scale, stageHeight);

        if (this._groups) {
            _.forOwn(this._groups, group => group.reset(this._metrics));
        }
    }
}

export const GEL_MIN_RATIO_WIDTH = 4;
export const GEL_MIN_RATIO_HEIGHT = 3;
export const GEL_SAFE_FRAME_RATIO = GEL_MIN_RATIO_WIDTH / GEL_MIN_RATIO_HEIGHT;

import fp from "../lib/lodash/fp/fp.js";

const getScale = fp.curry((scaleMethods, stageHeightPx, { width, height }) => {
    const scale = scaleMethods[width / height >= GEL_SAFE_FRAME_RATIO ? "wide" : "narrow"](width, height);
    return { width, height, scale, stageHeightPx };
});

const getBounds = (game) => () => game.scale.getParentBounds();

export function create(stageHeightPx, game) {
    // Will be immediately resized:
    game.scale.setGameSize(2, 2);
    game.scale.scaleMode = Phaser.ScaleManager.EXACT_FIT;

    const onScaleChange = new Phaser.Signal();

    const scaleMethods = {
        wide: (width, height) => height / stageHeightPx,
        narrow: (width, height) => width / stageHeightPx / GEL_SAFE_FRAME_RATIO,
    };

    const getSize = fp.flow(getBounds(game), fp.pick(["width", "height"]), getScale(scaleMethods, stageHeightPx));

    const setSize = ({ width, height, scale, stageHeightPx: stageHeight }) => {
        game.scale.setGameSize(width, height);
        onScaleChange.dispatch(width, height, scale, stageHeight);
    };

    const onSizeChange = fp.flow(getSize, setSize);

    game.scale.onSizeChange.add(onSizeChange);

    return {
        onScaleChange,
        getSize,
    };
}

import * as _ from "../lib/lodash/lodash.js";

export class Screen extends Phaser.State {
    get context() {
        return this._context;
    }

    set context(newContext) {
        this._context = _.merge({}, this._context, newContext);
    }

    init(context, next, layoutFactory) {
        this.layoutFactory = layoutFactory;
        this._context = context;
        this._next = next;
    }

    next(changedState) {
        this._next(changedState);
    }
}

import * as _ from "../lib/lodash/lodash.js";
import * as LayoutFactory from "./layout/factory.js";

export function create(
    game,
    context,
    transitions,
    gameWrapper,
)  {
    let currentScreen = transitions[0];

    const self = { getTransitions };
    const layoutFactory = LayoutFactory.create(game, gameWrapper);

    transitions.forEach(transition => game.state.add(transition.name, transition.state));

    const screenLookup = _.fromPairs(_.map(transitions, (c) => [c.name, c]));
    game.state.start(currentScreen.name, true, false, context, next, layoutFactory);

    return self;

    function next(changedState) {
        //TODO: Use GMI to save persistent state to local storage, if it has been updated
        const newState = _.merge({}, context.inState, changedState);
        const nextScreenName = currentScreen.nextScreenName(newState);
        context.inState = newState;
        layoutFactory.removeAll();
        game.state.start(nextScreenName, true, false, context, next, layoutFactory);

        currentScreen = screenLookup[nextScreenName];
    }

    function getTransitions() {
        return transitions;
    }
}

import fp from "../lib/lodash/fp/fp.js";

export const create = () => {
    const _bus = {};

    const addSignal = message => {
        if (!_bus[message.name]) {
            _bus[message.name] = new Phaser.Signal();
        }
        return message;
    };

    const remove = name => {
        _bus[name].dispose();
        delete _bus[name];
    };

    const addSubscription = message => _bus[message.name].add(message.callback);
    const publishMessage = message => _bus[message.name].dispatch(message.data);

    const subscribe = fp.flow(addSignal, addSubscription);
    const publish = fp.flow(addSignal, publishMessage);

    return { remove, subscribe, publish };
};

