export function startup(transitions, initialAdditionalState) {
    const gmi = window.getGMI({});
    const urlParams = parseUrlParams(window.location.search);
    const qaMode = { active: urlParams.qaMode ? urlParams.qaMode : false, testHarnessLayoutDisplayed: false };
    hookErrors(gmi.gameContainerId);

    const phaserConfig = {
        width: 1400,
        height: 600,
        renderer: Phaser.AUTO,
        antialias: true,
        multiTexture: false,
        parent: getContainerDiv(gmi),
        state: new Startup(gmi, onStarted),
    };
    // Keep the console tidy:
    window.PhaserGlobal = { hideBanner: true };

    const game = new Phaser.Game(phaserConfig);

    let resolvedPromise;

    return new Promise(resolve => {
        resolvedPromise = resolve;
    });

    function onStarted(config) {
        // Phaser is now set up and we can use all game properties.
        game.canvas.setAttribute("aria-hidden", "true");
        const context = {
            gmi,
            inState: _.merge({ transient: {}, persistent: {} }, initialAdditionalState),
            popupScreens: [],
            gameMuted: true,
            qaMode,
            sequencer: { getTransitions: () => [] },
        };
        context.sequencer = Sequencer.create(game, context, transitions, getContainerDiv(gmi));
        game.stage.backgroundColor = "#333";
        resolvedPromise(game);
    }
}

class Startup extends Phaser.State {
    constructor(gmi, onStarted) {
        super();
        this._gmi = gmi;
        this._onStarted = onStarted;
    }

    preload() {
        const gmi = this._gmi;
        this.game.load.baseURL = this._gmi.gameDir;

        // All asset paths are relative to the location of the config.json:
        const theme = gmi.embedVars.configPath;
        const configDir = theme.split(/([^/]+$)/, 2)[0];
        this.game.load.path = configDir;

        //this.load.json(CONFIG_KEY, configFile); xxx
    }

    create() {
        this._onStarted({} /* this.game.cache.getJSON(CONFIG_KEY) */);
    }
}

function hookErrors(gameDivId) {
    const containerDiv = document.getElementById(gameDivId) || document.body;
    let messageElement;

    window.addEventListener("error", event => {
        if (!messageElement) {
            messageElement = containerDiv.appendChild(document.createElement("pre"));
            const padding = "2em";
            const style = messageElement.style;
            style.position = "absolute";
            style.top = style.left = "0";
            style.backgroundColor = "black";
            style.color = "white";
            style.padding = padding;
            style.width = style.height = `calc(100% - 2 * ${padding})`;
        }
        messageElement.innerText = `Something isn't working:\n\n${event.error.message || event.error}\n\n${event.error
            .stack || ""}`;
    });
}

function getContainerDiv(gmi) {
    const containerDiv = document.getElementById(gmi.gameContainerId);
    if (!containerDiv) {
        throw Error(`Container element "#${gmi.gameContainerId}" not found`);
    } else {
        return containerDiv;
    }
}

import debounce from "../../lib/lodash/debounce.js";
import { accessibleDomElement } from "./accessible-dom-element.js";

export function accessibilify(button, ariaLabel) {
    const game = button.game;
    const accessibleElement = newAccessibleElement();
    const repositionElement = debounce(setElementPosition, 200);

    assignEvents();
    repositionElement();

    return button;

    function newAccessibleElement() {
        return accessibleDomElement({
            id: button.name,
            ariaLabel: ariaLabel ? ariaLabel : button.name,
            parent: game.canvas.parentElement,
            onClick: buttonAction,
        });
    }

    function setElementPosition() {
        accessibleElement.position(button.getBounds());
    }

    function assignEvents() {
        game.scale.onSizeChange.add(repositionElement);
        game.state.onStateChange.addOnce(teardown);
        button.update = checkBounds;
    }

    function teardown() {
        accessibleElement.remove();
        game.scale.onSizeChange.remove(repositionElement);
    }

    function checkBounds() {
        if (isOutsideScreen() && accessibleElement.visible()) {
            accessibleElement.hide();
        } else if (!isOutsideScreen() && !accessibleElement.visible()) {
            accessibleElement.show();
        }
    }

    function isOutsideScreen() {
        const pixiBounds = button.getBounds();
        const buttonBounds = new Phaser.Rectangle(pixiBounds.x, pixiBounds.y, pixiBounds.width, pixiBounds.height);

        return (
            buttonBounds.top > game.height ||
            buttonBounds.bottom < 0 ||
            buttonBounds.left > game.width ||
            buttonBounds.right < 0
        );
    }

    function buttonAction() {
        button.events.onInputUp.dispatch(button, game.input.activePointer, false);
    }
}

export function accessibleDomElement(options) {
    const el = document.createElement("div");

    init();

    return {
        el,
        hide,
        show,
        visible,
        position,
        remove,
    };

    function init() {
        el.setAttribute("tabindex", "0");
        el.setAttribute("aria-label", options.ariaLabel);
        el.setAttribute("role", "button");
        el.style.position = "absolute";
        el.style.cursor = "pointer";
        el.addEventListener("keyup", keyUp);
        el.addEventListener("click", options.onClick);
        options.parent.appendChild(el);
    }

    function hide() {
        el.setAttribute("tabindex", "-1");
        el.style.visibility = "hidden";
    }

    function show() {
        el.setAttribute("tabindex", "0");
        el.style.visibility = "visible";
    }

    function visible() {
        return el.style.visibility === "visible";
    }

    function position(positionOptions) {
        el.style.left = positionOptions.x.toString() + "px";
        el.style.top = positionOptions.y.toString() + "px";
        el.style.width = positionOptions.width.toString() + "px";
        el.style.height = positionOptions.height.toString() + "px";
    }

    function keyUp(event) {
        const enterKey = event.key === "Enter";
        const spaceKey = event.key === " ";

        if (enterKey || spaceKey) {
            options.onClick();
        }
    }

    function remove() {
        el.remove();
    }
}

export function parseUrlParams(paramsString) {
    if (!valid()) {
        return {};
    }

    const keyValues = paramsString.slice(paramsString.indexOf("?") + 1).split("&");
    return keyValues.reduce((params, hash) => {
        const [key, val] = hash.split("=");
        return Object.assign(params, { [key]: parseBooleans(val) });
    }, {});

    function parseBooleans(val) {
        const decodedComponent = decodeURIComponent(val);

        if (decodedComponent === "true") {
            return true;
        } else if (decodedComponent === "false") {
            return false;
        } else {
            return decodedComponent;
        }
    }

    function valid() {
        const hasQuestionMark = paramsString.indexOf("?") >= 0;
        const hasEqualsSymbol = paramsString.indexOf("=") >= 0;

        return hasQuestionMark && hasEqualsSymbol;
    }
}

